import _forEach from 'lodash/forEach'; /* vim: set softtabstop=2 shiftwidth=2 expandtab : */

export default (function (vueElement, googleMapObject, events) {
  _forEach(events, function (eventName) {
    var exposedName = eventName;
    googleMapObject.addListener(eventName, function (ev) {
      vueElement.$emit(exposedName, ev);
    });
  });
});